import React, { Component } from 'react';
import '../../../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import ProductInstance from '../../../interface/product_management';
import web3 from '../../../web3';

class BuildParts extends Component {
    state = {
        serial : '',
        accounts: [],
        parts : []
    };

    async componentDidMount() {
        const accounts = await web3.eth.getAccounts();
        this.setState({ accounts });
    }

    onChange = (e) =>{
        this.setState({[e.target.name] : e.target.value})
    }

    onSubmit = async (e) => {
        e.preventDefault();
        console.log(this.state.serial);
        console.log(this.state.type);
        const details = await ProductInstance.methods.buildPart(this.state.serial,this.state.type)
            .send({ from: this.state.accounts[0]})
            .then(() => ProductInstance.methods.showPart(this.state.serial)
            .call({from : this.state.accounts[0]}))
        console.log(details);        
    }

    render() {
        return (
            <div className="container mx-auto">
                <h1 className="text-uppercase my-10">hello from BuildParts</h1>
                <p>Your current account address is : {this.state.accounts}</p>
                <form onSubmit={this.onSubmit}>
                    <label htmlFor="Serial NUmber">Serial Number</label>
                    <input type="text" className="form-control" name="serial" onChange={this.onChange} />
                    <label htmlFor="type">Type</label>
                    <input type="text" className="form-control" name="type" onChange={this.onChange} />
                    <button className="my-2">Submit</button>
              </form>
            </div>
        )
    }
}

export default BuildParts;
