import React, { Component } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import web3 from '../../web3';

class BuildParts extends Component {
    state = {
        serial : '',
        accounts: []
    };

    async componentDidMount() {
        const accounts = await web3.eth.getAccounts();
        this.setState({ accounts });
    }

    onChange = (e) =>{
        console.log(this);
        this.setState({serial : e.target.value})
    }

    onSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.serial);
    }


    render() {

        return (
            <div className="container mx-auto">
                <h1 className="text-uppercase my-10">hello from BuildParts</h1>
                <p>Your current account address is : {this.state.accounts}</p>
                <form onSubmit={this.onSubmit}>
                    <label htmlFor="Serial NUmber">Serial Number</label>
                    <input type="text" className="form-control" id="serial" onChange={this.onChange} />
                    <label htmlFor="type">Type</label>
                    <input type="text" className="form-control" id="type" onChange={this.onChange} />
                    <button>Submit</button>
              </form>
            </div>
        )
    }
}

export default BuildParts;
