import React, { Component } from 'react';
import web3 from '../../web3';

class BuildParts extends Component {
    state = {
        serial,
        accounts: []
    };

    async componentDidMount() {
        const accounts = await web3.eth.getAccounts();
        this.setState({ accounts });
    }

    onChange = (e) =>{
        this.setState({serail : e.target.value})
    }

    onSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.serail);
    }


    render() {

        return (
            <div className="container">
                <h1 className="text-uppercase">hello from BuildParts</h1>
                <p>Your current account address is : {this.state.accounts}</p>
                <form onSubmit={this.onSubmit}>
                    <label htmlFor="Serial NUmber">Serail Number</label>
                    <input type="number" className="form-control" id="serail" onChange={this.onChange} />
              </form>
            </div>
        )
    }
}

export default BuildParts;
