import React, { Component } from 'react';
import ChangeOwnerShipInstance from '../interface/change_ownership';


class AddOwnerShip extends Component {
    state = {
        serial: '',
        type:'',
        loading: false
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    onSubmit = (e) => {
        e.preventDefault();
        this.setState({ loading: true});
        ChangeOwnerShipInstance.methods.addOwnership(this.state.type, this.state.serial)
            .send({ from: this.state.accounts[0] }, (err) => {
                this.setState({ loading: false });
                if (err) {
                    this.setState({ message: "Add Ownership failed" });
                }
                else {
                    this.setState({ message: "Add Ownership Succesful" });
                }
                this.setState({ serial: '', type: '' });
                setTimeout(() => this.setState({ message: '' }), 3000)
            })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <h1>Get Parts Details</h1>
                    <div>
                        <a href="/" className="btn btn-outline-primary btn-sm">Build Parts</a>
                    </div>
                    <label htmlFor="Serial NUmber">Serial Number</label>
                    <input type="text" className="form-control" name="type"
                        value={type} onChange={this.onChange} />
                    <label htmlFor="Serial NUmber">Type 0 for Part and 1 for Product</label>
                    <input type="text" className="form-control" name="serial"
                        value={serial} onChange={this.onChange} />
                    <button className="btn btn-primary my-2">Get Details</button>
                    {loading && <Spinner />}
                </form>
            </div>
        )
    }
}

export default AddOwnerShip;
